mapper.insert:
INSERT INTO cleangrade (
  RESIDX, RESNAME, RESCLEANSCORE, ASSIGNYEAR, ADDRGU, ADDRDONG, OLDADDR, NEWADDR, NUMADDR, XPOS, YPOS, DELYN
) 
VALUES (
  #{RESIDX}, #{RESNAME}, #{RESCLEANSCORE}, #{ASSIGNYEAR}, #{ADDRGU}, #{ADDRDONG}, #{OLDADDR}, #{NEWADDR}, #{NUMADDR}, #{XPOS}, #{YPOS}, #{DELYN}
)

mapper.select:
SELECT COUNT(*) FROM CLEANGRADE
WHERE RESNAME = #{RESNAME}
  AND NEWADDR = #{NEWADDR}
  AND ASSIGNYEAR = #{ASSIGNYEAR}

mapper.update:
UPDATE CLEANGRADE
SET clDelYn = 'N'
WHERE RESNAME = #{RESNAME}
  AND NEWADDR = #{NEWADDR}        
  AND ASSIGNYEAR = #{ASSIGNYEAR}

UPDATE CLEANGRADE
SET RESCLEANSCORE = #{RESCLEANSCORE},
    DELYN = 'N'
WHERE RESNAME = #{RESNAME}
  AND NEWADDR = #{NEWADDR}
  AND ASSIGNYEAR = #{ASSIGNYEAR}
